mapper.select:
select 

from lh_sys_region
where 1 = 1

    and parent_id = #{parentId,jdbcType=INTEGER}


    and region_name = #{regionName,jdbcType=VARCHAR}


    and region_order = #{regionOrder,jdbcType=INTEGER}


    and region_level = #{regionLevel,jdbcType=INTEGER}


    and region_code = #{regionCode,jdbcType=VARCHAR}

mapper.insert:
insert into lh_sys_region (region_id, parent_id, region_name, 
  region_order, region_level, region_code
  )
values (#{regionId,jdbcType=INTEGER}, #{parentId,jdbcType=INTEGER}, #{regionName,jdbcType=VARCHAR}, 
  #{regionOrder,jdbcType=INTEGER}, #{regionLevel,jdbcType=INTEGER}, #{regionCode,jdbcType=VARCHAR}
  )
